one sig Ord {
   First:  set elem,
   Next: elem -> elem,
   loop: elem -> elem,
} {
	-- PUT THAT THE NEXT MODELS THE RELATION
	all s:elem | all s':Next[s] |  someTrans[s.ins1, s'.ins1] and someTrans[s.ins2, s'.ins2] and someTrans[s.ins3, s'.ins3] and someTrans[s.ins4, s'.ins4] and someTrans[s.ins5, s'.ins5]

 -- the following is a requirement over the possible executions, we have to respect 
    all s:elem | all s':Next[s] | (localTrans[s.ins1, s'.ins1] iff (envTrans[s.ins2,s'.ins2] and envTrans[s.ins3,s'.ins3]  and envTrans[s.ins4,s'.ins4] and envTrans[s.ins5,s'.ins5] )) 
                                           and  
                                          (localTrans[s.ins2, s'.ins2] iff (envTrans[s.ins1,s'.ins1] and envTrans[s.ins3,s'.ins3]  and envTrans[s.ins4,s'.ins4] and envTrans[s.ins5,s'.ins5]))
										    and  
                                          (localTrans[s.ins3, s'.ins3] iff (envTrans[s.ins1,s'.ins1] and envTrans[s.ins2,s'.ins2]  and envTrans[s.ins4,s'.ins4] and envTrans[s.ins5,s'.ins5]))
										   and  
                                          (localTrans[s.ins4, s'.ins4] iff (envTrans[s.ins1,s'.ins1] and envTrans[s.ins2,s'.ins2]  and envTrans[s.ins3,s'.ins3] and envTrans[s.ins5,s'.ins5]))
										   and  
                                          (localTrans[s.ins5, s'.ins5] iff (envTrans[s.ins1,s'.ins1] and envTrans[s.ins2,s'.ins2]  and envTrans[s.ins3,s'.ins3] and envTrans[s.ins4,s'.ins4]))




 -- and requirements over the trace
   pred/totalOrder[elem,First,Next]
   and loop= last -> back
}

-- it says that the the transition is local
pred localTrans[n,n':Node]{
	n' in ((InstanceNoName.local)[n])
}

-- it says that the the transition is non-local
pred envTrans[n,n':Node]{
	(n' in ((InstanceNoName.env)[n])) or  (n'= n)
}

-- local or non-local transitions including stuttering
pred someTrans[n,n':Node]{
	localTrans[n,n'] or envTrans[n,n'] 
}

lone sig back in elem {}

fun first: one elem { Ord.First }

fun last: one elem { elem - ((Ord.Next).elem) }

fun next : elem->elem { Ord.Next + Ord.loop }

fun prev : elem->elem { ~this/next }

fun past : elem->elem { ^(~this/next) }

fun future : elem -> elem { elem <: *this/next }

pred infinite {
  some loop
}

pred finite {
  no loop
}


